import styled from "styled-components"
import FacebookIcon from '@mui/icons-material/Facebook';
import InstagramIcon from '@mui/icons-material/Instagram';
import TwitterIcon from '@mui/icons-material/Twitter';

const Container = styled.div`
    display: flex;
`
const Left = styled.div`
    flex: 1;
    display: flex;
    flex-direction: column;
    padding: 20px;
`
const Logo =styled.h1`
`
const Description =styled.p`
    margin: 20px 0;
`
const SocialContainer =styled.div`
    display: flex;
`
const SocialIcon =styled.div`
    width: 40px;
    height: 40px;
    border-radius: 50%;
    color: white;
    background-color: #${props=>props.color};
    display: flex;
    align-items: center;
    justify-content: center;
    margin-right: 20px;
`
const Center = styled.div`
    flex: 1;
    padding: 20px;
`
const Title = styled.h3`

`
const List = styled.ul`
    
`
const Right = styled.div`
    flex: 1;
`

function Footer() {
  return (
    <Container>
        <Left>
            <Logo>OnlyNeons</Logo>
            <Description>Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make</Description>
            <SocialContainer>
                <SocialIcon color="3B5999"><FacebookIcon /></SocialIcon>
                <SocialIcon color="E4405F"><InstagramIcon /></SocialIcon>
                <SocialIcon color="55ACEE"><TwitterIcon /></SocialIcon>
            </SocialContainer>
        </Left>
        <Center></Center>
        <Right></Right>
    </Container>
  )
}

export default Footer
