import axios from "axios";

const BASE_URL = "http://localhost:5000/backend";

const user = JSON.parse(localStorage.getItem("persist:root"))?.user;
const currentUser = user && JSON.parse(user).currentUser;
const TOKEN =
  "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYyYTBiMjdmMDExOGMwODk5ZWJjZmM2YSIsImlzQWRtaW4iOmZhbHNlLCJpYXQiOjE2NTUxNDE1MjMsImV4cCI6MTY1NTQwMDcyM30.1EpIUmjIkjIlOJz0GKR2sT66LXEqHWaFGbyKtpbeAE0";
// currentUser?.accessToken;
// accessToken: ""

export const publicRequest = axios.create({
  baseURL: BASE_URL,
});

export const userRequest = axios.create({
  baseURL: BASE_URL,
  header: { token: `Bearer ${TOKEN}` },
});
