import styled from "styled-components"

const Container = styled.div`
    height: 30px;
    width: "100vw";
    background-color: teal;
    color: white;
    text-align: center;
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 14px;
    font-weight: 500;
    position: sticky;
    top:0;
    left:0;
    right:0;
    z-index:10;
 
`

const Announcement = () => {
  return (
    <Container>
        Super Deal ! Free Shipping on Orders Over $50
    </Container>
  )
}

export default Announcement
