import { createSlice } from "@reduxjs/toolkit";

const cartSlice = createSlice({
  name: "cart",
  initialState: {
    product: {},
    products: [],
    quantity: 0,
    shipping: 5,
    subtotal: 0,
    total: 0,
  },
  reducers: {
    addProduct: (state, action) => {
      state.quantity += 1;
      state.product = action.payload;
      state.products.push(state.product);
      state.subtotal += state.product * state.product.quantity;
      state.total += state.subtotal;
      state.total < 50
        ? (state.total += state.shipping)
        : (state.total = state.subtotal);
    },
    emptyCart: (state) => {
      state.quantity = 0;
      state.products = [];
      state.total = 0;
      state.subtotal = 0;
    },
    // increaseProduct: (state, action) =>{
    //   state.product
    // }
  },
});

export const { addProduct, emptyCart } = cartSlice.actions;
export default cartSlice.reducer;
