import axios from "axios";

const BASE_URL = "http://localhost:5000/backend";

const user = JSON.parse(localStorage.getItem("persist:root"))?.user;
const currentUser = user && JSON.parse(user).currentUser;
// const TOKEN = currentUser?.accessToken;
const TOKEN =
  "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYyYTBjYjEyZGJlN2Q1OWRhOGQwZmIwNiIsImlzQWRtaW4iOnRydWUsImlhdCI6MTY1NTIwMjc4OSwiZXhwIjoxNjU1NDYxOTg5fQ.GpVPWB_xAsaHOf7PYzeDpMspPktmLB1dqJSeP6FItok";

export const publicRequest = axios.create({
  baseURL: BASE_URL,
});

export const userRequest = axios.create({
  baseURL: BASE_URL,
  headers: { token: `Bearer ${TOKEN}` },
});
